package mymain;


public class _05_문자열자료형 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//문자열 자료형 : String
		//				: 1개 이상의 문자를 나열한 데이터
		//상수 			: "A" 'A'
		//"A"=String 'A'=Char
		//이름을 출력하려다 보면 따로 할수는 없지 않느냐 .
		//실제 구조는 배열로 'A''B''C'로 저장이된다
		//출력서식 		: %s
		
		//순서1
		String name = "김영주";
		System.out.printf("내 이름은[%s]입니다\n" , name);
		
		//개인정보 같은 것을 표현 할 수 있다.
		//이름 String,  나이 int, 키 double, 결혼유무  boolean, 주소 String
		//단 주소는 공백도 포함을 해야한다.
 		
	
		
		
		
		System.out.println();
		
		System.out.println();

	}

}
